SELECT /* [dXm.display.category.xml][selectDisplayReviewList][카테고리 상품 리뷰 카운트][ken] */ G.GOD_NO , G.GOD_NM AS GOD_NM , G.GOD_EVL_TURN , G.GOD_EVL_WRITNG_DT , G.DSGN_GRP_NO , G.MBR_NO , G.GOD_EVL_SJ , G.GOD_EVL_CONT , (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'SIZE_EVL' AND CD= G.SIZE_EVL_CD AND LANG_CD =#{lang,jdbcType=VARCHAR}) AS SIZE_EVL_CD , (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'COLOR_EVL' AND CD= G.COLOR_EVL_CD AND LANG_CD =#{lang,jdbcType=VARCHAR}) AS COLOR_EVL_CD , G.QLTY_EVL_SCORE , G.NTCE_YN , G.INQIRE_NUM , G.BST_GOD_EVL_YN , G.MALL_ID , G.CSTMR_NM , G.REG_DT , SUBSTR (G.MBR_ID, 0, LENGTH (G.MBR_ID) - 2) || '**' AS MBR_ID , G.ITM_NM , G.RTL_PRC , G.LAST_SALE_PRC , G.ORD_QTY , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 1 AND GI.IMG_USE_YN = 'Y') AS imgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'DETAIL' AND GI.IMG_USE_YN = 'Y' AND ROWNUM = 1) AS detailImgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 2 AND GI.IMG_USE_YN = 'Y') AS imgBackUrl , '/goods/'|| (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= G.RECOMEND_SEX_CD AND LANG_CD = #{lang,jdbcType=VARCHAR}) || '/' || (SELECT SB.BRND_PRDLST_NM FROM SYS_BRND_PRDLST SB WHERE SB.BRND_ID = G.BRND_ID AND SB.PRDLST_CD = G.PRDLST_CD)|| '/'|| G.ERP_GOD_NO AS godUrl , G.COLOR_CD FROM ( SELECT b.* , ROWNUM AS NO FROM ( SELECT GGE.GOD_NO , CASE WHEN #{lang,jdbcType=VARCHAR} ='KOR' THEN GD.GOD_NM ELSE GLGN.GOD_NM END AS GOD_NM , GGE.GOD_EVL_TURN , GGE.GOD_EVL_WRITNG_DT , GGE.DSGN_GRP_NO , GGE.MBR_NO , GGE.GOD_EVL_SJ , GGE.GOD_EVL_CONT , GGE.SIZE_EVL_CD , GGE.COLOR_EVL_CD , GGE.QLTY_EVL_SCORE , GGE.NTCE_YN , GGE.INQIRE_NUM , GGE.BST_GOD_EVL_YN , GGE.MALL_ID , GGE.CSTMR_NM , GGE.REG_DT , M.MBR_ID , OG.ITM_NM , GD.RECOMEND_SEX_CD , DGP.RTL_PRC , DGP.LAST_SALE_PRC , OG.ORD_QTY , GD.BRND_ID , GD.PRDLST_CD , GD.ERP_GOD_NO , GD.COLOR_CD FROM GOD_GOD_EVL GGE JOIN ORD_GOD OG ON OG.ORD_NO = GGE.ORD_NO AND OG.GOD_NO = GGE.GOD_NO AND OG.ORD_GOD_TURN = GGE.ORD_GOD_TURN JOIN GOD GD ON GD.GOD_NO = GGE.GOD_NO JOIN DSP_GOD_PRC DGP ON GD.GOD_NO = DGP.GOD_NO AND DGP.MALL_ID = 'DXM' AND DGP.LANG_CD = 'KOR' AND DGP.PRC_SECT_CD = #{prcSectCd, jdbcType=VARCHAR} JOIN MBR M ON GGE.MBR_NO = M.MBR_NO LEFT OUTER JOIN GOD_LANGBY_GOD_NM GLGN ON OG.GOD_NO = GLGN.GOD_NO AND GLGN.LANG_CD = #{lang,jdbcType=VARCHAR} AND GLGN.OVSEA_DSP_STAT_CD = 'DSP_APRV' WHERE GGE.GOD_NO IS NOT NULL
ORDER BY GGE.BST_GOD_EVL_SORT_SEQ, GGE.GOD_EVL_SORT_SEQ, GGE.REG_DT DESC ) b
