SELECT G.* /* [dXm.plan.xml][selectDxmPlanSprtrGodList][기획전 상품 목록 ][ken] */ , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 1 AND GI.IMG_USE_YN = 'Y') AS imgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'DETAIL' AND GI.IMG_USE_YN = 'Y' AND ROWNUM = 1) AS detailImgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 2 AND GI.IMG_USE_YN = 'Y') AS imgBackUrl
FROM (SELECT GD.GOD_NO , DGP.LANG_CD , CASE WHEN #{lang,jdbcType=VARCHAR}='KOR' THEN CASE WHEN GD.PRDLST_CD IN('SH','BK','SG','MR','CR','HS','GY','EC','LS','CP','BN','HT','GL','SO','MS','ML','AC') OR INSTR(GD.GOD_NM ,(SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD = #{lang,jdbcType=VARCHAR} ), 1) = 1 THEN GD.GOD_NM ELSE (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='KOR' ) || ' ' || GD.GOD_NM END ELSE CASE WHEN GD.PRDLST_CD IN('SH','BK','SG','MR','CR','HS','GY','EC','LS','CP','BN','HT','GL','SO','MS','ML','AC') OR INSTR(GD.GOD_NM ,(SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD = #{lang,jdbcType=VARCHAR} ), 1) = 1 THEN GLGN.GOD_NM ELSE (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='ENG' ) || ' ' || GLGN.GOD_NM END END AS GOD_NM , GD.GOD_SALE_SECT_CD , DGP.RTL_PRC , DGP.LAST_SALE_PRC , DP.SORT_SEQ AS DP_SORT_SEQ , DPG.SORT_SEQ , '/goods/'|| (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='KOR') || '/' || (SELECT SB.BRND_PRDLST_NM FROM SYS_BRND_PRDLST SB WHERE SB.BRND_ID = GD.BRND_ID AND SB.PRDLST_CD = GD.PRDLST_CD)|| '/'|| GD.ERP_GOD_NO AS godUrl , GD.GOD_TP_CD , DPG.SPRTR_TURN , GD.REG_DT FROM GOD GD LEFT OUTER JOIN GOD_LANGBY_GOD_NM GLGN ON GD.GOD_NO = GLGN.GOD_NO AND GLGN.LANG_CD = #{lang,jdbcType=VARCHAR} AND GLGN.OVSEA_DSP_STAT_CD = 'DSP_APRV' JOIN DSP_GOD_PRC DGP ON GD.GOD_NO = DGP.GOD_NO AND DGP.MALL_ID = 'DXM' AND DGP.LANG_CD = 'KOR' AND DGP.PRC_SECT_CD = #{prcSectCd, jdbcType=VARCHAR} JOIN DSP_PROMT_GOD DPG ON GD.GOD_NO = DPG.GOD_NO JOIN DSP_PROMT_SPRTR DP ON DP.PROMT_SN = DPG.PROMT_SN AND DP.SPRTR_TURN = DPG.SPRTR_TURN WHERE GD.GOD_APRV_SECT_CD = 'APRV_COMPT' AND GD.DSP_YN ='Y' AND GD.GOD_SALE_SECT_CD != 'SALE_END' AND DPG.DSP_YN ='Y' AND DP.DSP_YN ='Y' AND DPG.PROMT_SN = #{promtSn,jdbcType=NUMERIC} AND DPG.SPRTR_TURN = #{sprtrTurn,jdbcType=NUMERIC}
AND GLGN.OVSEA_DSP_STAT_CD = 'DSP_APRV'
) G
ORDER BY G.DP_SORT_SEQ,G.SPRTR_TURN, G.SORT_SEQ, G.REG_DT DESC

SELECT G.* /* [dXm.plan.xml][selectDxmEvtAplGodList][이벤트 상품 목록][ken] */ , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 1 AND GI.IMG_USE_YN = 'Y') AS imgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'DETAIL' AND GI.IMG_USE_YN = 'Y' AND ROWNUM = 1) AS detailImgFrontUrl , (SELECT GI.IMG_URL FROM GOD_IMG GI WHERE G.GOD_NO = GI.GOD_NO AND GI.IMG_TP_CD = 'THNAIL' AND GI.IMG_TURN = 2 AND GI.IMG_USE_YN = 'Y') AS imgBackUrl
FROM (SELECT GD.GOD_NO , DGP.LANG_CD , CASE WHEN #{lang,jdbcType=VARCHAR}='KOR' THEN CASE WHEN GD.PRDLST_CD IN('SH','BK','SG','MR','CR','HS','GY','EC','LS','CP','BN','HT','GL','SO','MS','ML','AC') OR INSTR(GD.GOD_NM ,(SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD = #{lang,jdbcType=VARCHAR} ), 1) = 1 THEN GD.GOD_NM ELSE (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='KOR' ) || ' ' || GD.GOD_NM END ELSE CASE WHEN GD.PRDLST_CD IN('SH','BK','SG','MR','CR','HS','GY','EC','LS','CP','BN','HT','GL','SO','MS','ML','AC') OR INSTR(GD.GOD_NM ,(SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD = #{lang,jdbcType=VARCHAR} ), 1) = 1 THEN GLGN.GOD_NM ELSE (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='ENG' ) || ' ' || GLGN.GOD_NM END END AS GOD_NM , GD.GOD_SALE_SECT_CD , DGP.RTL_PRC , DGP.LAST_SALE_PRC , ERG.SORT_SEQ , '/goods/'|| (SELECT CD_NM FROM MV_SYS_CD WHERE UPPER_CD = 'RECOMEND_SEX' AND CD= GD.RECOMEND_SEX_CD AND LANG_CD ='KOR') || '/' || (SELECT SB.BRND_PRDLST_NM FROM SYS_BRND_PRDLST SB WHERE SB.BRND_ID = GD.BRND_ID AND SB.PRDLST_CD = GD.PRDLST_CD)|| '/'|| GD.ERP_GOD_NO AS godUrl , GD.GOD_TP_CD , ES.SORT_SEQ AS ES_SORT_SEQ , GD.REG_DT , ERG.SPRTR_TURN FROM GOD GD LEFT OUTER JOIN GOD_LANGBY_GOD_NM GLGN ON GD.GOD_NO = GLGN.GOD_NO AND GLGN.LANG_CD = #{lang,jdbcType=VARCHAR} AND GLGN.OVSEA_DSP_STAT_CD = 'DSP_APRV' JOIN DSP_GOD_PRC DGP ON GD.GOD_NO = DGP.GOD_NO AND DGP.MALL_ID = 'DXM' AND DGP.LANG_CD = 'KOR' AND DGP.PRC_SECT_CD = #{prcSectCd, jdbcType=VARCHAR} JOIN EVT_RELATE_GOD ERG ON GD.GOD_NO = ERG.GOD_NO JOIN EVT_SPRTR ES ON ES.EVT_NO = ERG.EVT_NO AND ERG.SPRTR_TURN = ES.SPRTR_TURN WHERE GD.GOD_APRV_SECT_CD = 'APRV_COMPT' AND GD.DSP_YN ='Y' AND GD.GOD_SALE_SECT_CD != 'SALE_END' AND ERG.DSP_YN ='Y' AND ES.DSP_YN = 'Y' AND ERG.EVT_NO = #{evtNo} AND ERG.SPRTR_TURN = #{sprtrTurn}
AND GLGN.OVSEA_DSP_STAT_CD = 'DSP_APRV'
) G
